import React,{useState} from 'react'
import{HeroContainer,HeroBg,VideoBg,HeroContent,HeroH1,
HeroP,HeroBtnWrapper,ArrowForward,ArrowRight} from './Hero'
import Video from '../../video/video.mp4'
import {Button} from '../ButtonElements'
function Hero() {
    const[hover,setHover]=useState(false)
    const onMouse=()=>{
        setHover(!hover)
    }
    return (
        <>
        <HeroContainer id="home">
            <HeroBg>
                <VideoBg autoPlay loop muted src={Video}
                type='video/mp4'/>
            </HeroBg>
            <HeroContent>
                <HeroH1>
                   بانکداری مجازی خیلی راحت وآسونه 
                </HeroH1>
                <HeroP>
                   هر چه سریع تر برای ثبت نام اقدام کن
                </HeroP>
                <HeroBtnWrapper>
                    <Button onMouseEnter={onMouse}
                    onMouseLeave={onMouse}
                    to='signup'>
                     شروع کن{hover?<ArrowForward/>:<ArrowRight/>}
                    </Button>
                </HeroBtnWrapper>
            </HeroContent>
            </HeroContainer>    
        </>
    )
}

export default Hero
