import styled from 'styled-components'
export const HeroContainer=styled.div`
position: relative;
height: 800px;
padding: 0 30px;
background: #0c0c0c;
display: flex;
justify-content: center;
align-items: center;
z-index: 1;
`
export const HeroBg=styled.div`
position: absolute;
top:0;
bottom: 0;
left: 0;
right: 0;
width: 100%;
overflow: hidden;
background: red;
`
export const VideoBg=styled.video`
width: 100%;
height: 100%;
-o-object-fit:cover;
object-fit: cover;
background: #232a34;

`
